    style,
    onChange,
    errorMsg,
  } = props;

  //style
  const errorStyle = {
    textAlign: "right",
    display: "block",
    paddingBottom: "10px",
    color: "red",
    fontStyle: "italic",
    fontSize: "12px",
  };

  return (
    <label>
      <p>
        Họ và tên<span>*</span>
      </p>
      <input type="text" placeholder="Họ và tên bạn" />
    </label>
  );
}
