import React, { useState } from "react";

import { Link } from "react-router-dom";
import Modal from "../../pages/Modal";
import Register from "../../pages/Modal/component/register";

// import { createPortal } from "react-dom";

export default function HeaderContent() {
  let [isMenuOpen, setIsMenuOpen] = useState(false);

  function menuClick(left, overlay, visible) {
    //Open or Close
    document.querySelector(".nav").style.left = left;
    document.querySelector(".overlay_nav").style.opacity = overlay;
    document.querySelector(".overlay_nav").style.visibility = visible;
    setIsMenuOpen(!isMenuOpen);
  }

  //Modal
  let [open, setOpen] = useState("close");

  function registerModal(open) {
    setOpen(open);
    document.getElementById("root").style.position =
      open === "open" ? "fixed" : "initial";
  }

  const closeModalStyle = {
    color: "black",
    position: "absolute",
    right: "30%",
  };
  console.log(open);
  return (
    <>
      <header id="header">
        <div className="wrap">
          <div className="menu-hambeger">
            <div className="button">
              <span />
              <span />
              <span />
            </div>
            <span className="text">menu</span>
          </div>
          <a href="/#" className="logo">
            <img src="img/logo.svg" alt="" />
            <h1>CFD</h1>
          </a>
          <div className="right">
            <div className="have-login">
              <div className="account">
                <a href="/#" className="info">
                  <div className="name">Trần Lê Trọng Nghĩa</div>
                  <div className="avatar">
                    <img src="img/avt.png" alt="" />
                  </div>
                </a>
              </div>
              <div className="hamberger"></div>
              <div className="sub">
                <a href="/#">Khóa học của tôi</a>
                <a href="/#">Thông tin tài khoản</a>
                <a href="/#">Đăng xuất</a>
              </div>
            </div>
            {/* <div class="not-login bg-none">
                    <a href="/#" class="btn-register">Đăng nhập</a>
                    <a href="/login.html" class="btn main btn-open-login">Đăng ký</a>
                </div> */}
          </div>
        </div>
      </header>
      <nav className="nav">
        <ul>
          <li className="li_login">
            <a href="/#">Đăng nhập</a>
            <a href="/#">Đăng ký</a>
          </li>
          <li className="active">
            <a href="/#">Trang chủ</a>
          </li>
          <li>
            <a href="/#">CFD Team</a>
          </li>
          <li>
            <a href="/#">Khóa Học</a>
          </li>
          <li>
            <a href="/#">Dự Án</a>
          </li>
          <li>
            <a href="/#">Liên hệ</a>
          </li>
        </ul>
      </nav>
      <div className="overlay_nav" />
      <div
        className="overlay_nav"
        onClick={() => menuClick("0", 0, "hidden")}
      />
    </>
  );
}
