import React, { useState } from "react";

import { Link } from "react-router-dom";
import Modal from "../../pages/Modal";
import Register from "../../pages/Modal/component/register";

// import { createPortal } from "react-dom";

export default function HeaderContent() {
  let [isMenuOpen, setIsMenuOpen] = useState(false);

  function menuClick(left, overlay, visible) {
    //Open or Close
    document.querySelector(".nav").style.left = left;
    document.querySelector(".overlay_nav").style.opacity = overlay;
    document.querySelector(".overlay_nav").style.visibility = visible;
    setIsMenuOpen(!isMenuOpen);
  }

  //Modal
  let [open, setOpen] = useState("close");

  function registerModal(open) {
    setOpen(open);
    document.getElementById("root").style.position =
      open === "open" ? "fixed" : "initial";
  }

  const closeModalStyle = {
    color: "black",
    position: "absolute",
    right: "30%",
  };
  console.log(open);
  return (
    <>
      <header>
        <div className="container-fluid">
          <div
            className="menu"
            onClick={() =>
              !isMenuOpen
                ? menuClick("320px", 0.8, "visible")
                : menuClick("0", 0, "hidden")
            }
          >
            <div className="burger">
              <span />
              <span />
              <span />
            </div>
            <div className="text">Menu</div>
          </div>
          <a href="/#" className="logo">
            <img src="/img/logo.svg" alt="black" />
          </a>
          <div className="user">
            <a href="/#" className="btn btn-signin">
              Đăng nhập
            </a>
            <a
              href="/#"
              className="btn btn-register"
              onClick={() => registerModal("open")}
            >
              Đăng ký
            </a>
            <Modal open={open}>
              <button
                style={closeModalStyle}
                onClick={() => registerModal("close")}
              >
                x
              </button>
              <Register />
            </Modal>
          </div>
        </div>
      </header>

      <nav className="nav">
        <ul>
          <li>
            <Link to="/" onClick={() => menuClick("0", 0, "hidden")}>
              Trang chủ
            </Link>
          </li>
          <li>
            <Link to="/course" onClick={() => menuClick("0", 0, "hidden")}>
              Khóa học
            </Link>
          </li>
          <li>
            <Link to="/team" onClick={() => menuClick("0", 0, "hidden")}>
              CFD Team
            </Link>
          </li>
          <li>
            <Link to="/#" onClick={() => menuClick("0", 0, "hidden")}>
              Hỏi đáp
            </Link>
          </li>
          <li>
            <Link to="/collaborate" onClick={() => menuClick("0", 0, "hidden")}>
              Hợp tác
            </Link>
          </li>
        </ul>
      </nav>
      <div
        className="overlay_nav"
        onClick={() => menuClick("0", 0, "hidden")}
      />
    </>
  );
}
