const milisec_to_date = (date, format) => {
  let arrayDate = date.split("-");
  arrayDate[2] = parseInt(arrayDate[2]) + (18 / 3) * 7;

  const counting = (arrayDate) => {
    let endDay = new Date(arrayDate.join("-")).getTime();
    let startDay = new Date(date).getTime();
    let now = Date.now();

    if (endDay < now) {
      let rate = "khoa học đã hoàn thành";
      return rate;
    } else {
      let rate = `${parseInt(((now - startDay) * 100) / (endDay - startDay))}%`;
      return rate;
    }
  };

  switch (format) {
    case "yyyy-mm-dd":
      if (arrayDate[2] > 29) {
        switch (arrayDate[1]) {
          case 2:
            arrayDate[2] = arrayDate[2] - 29;
            arrayDate[1] = arrayDate[1] + 1;
            counting(arrayDate);

            break;
          case 1:
            arrayDate[2] = arrayDate[2] - 31;
