import React, { useState } from "react";
import FormField from "../../../../atom/formField";
import useFormValidate from "../../../../hook/useFormValidate";

export default function Info() {
  let [form, inputChange, error, submit] = useFormValidate(
    {
      name: "",
      phone: "",
      fb: "",
      skype: "",
    },
    {
      rule: {
        name: {
          required: true,
        },
        phone: {
          required: true,
          pattern: "phone",
        },
        email: {
          required: true,
          pattern: "email",
        },
        fb: {
          required: true,
          pattern: "facebook",
        },
      },
      message: {
        name: {
          pattern: "Ho va ten không đúng định dạng",
        },
      },
    }
  );

  return (
    <div className="tab1">
      <FormField
        label="Họ và Tên"
        type="text"
        name="name"
        placeholder="Nguyễn Văn A"
        required
        onChange={(e) => inputChange(e)}
        errorMsg={error.name}
      />
      <FormField
        label="Số điện thoại"
        type="text"
        name="phone"
        placeholder="0949******"
        required
        onChange={(e) => inputChange(e)}
        errorMsg={error.phone}
      />
      <FormField
        label="Email"
        type="text"
        name="email"
        placeholder="vuong.dang@dna.vn"
        required
        onChange={(e) => inputChange(e)}
        errorMsg={error.email}
      />
      <FormField
        label="Facebook"
        type="text"
        name="facebook"
        placeholder="Facebook url"
        required
        onChange={(e) => inputChange(e)}
        errorMsg={error.facebook}
      />
      <FormField
        label="Skype"
        type="text"
        name="skype"
        placeholder="Skype url"
        required
        onChange={(e) => inputChange(e)}
        errorMsg={error.website}
      />

      <div className="btn main rect" onClick={() => submit()}>
        LƯU LẠI
      </div>
    </div>
  );
}
