import React from "react";

export default function FormField(props) {
  const {
    label,
    required,
    type,
    name,
    placeholder,
    style,
    onChange,
    errorMsg,
  } = props;

  //style
  const errorStyle = {
    textAlign: "right",
    display: "block",
    paddingBottom: "10px",
    color: "red",
    fontStyle: "italic",
    fontSize: "12px",
  };

  return (
    <>
      <label>
        <p>
          {label}
          <span>*</span>
        </p>
        <input
          type={type}
          name={name}
          // defaultValue
          placeholder={placeholder}
          style={style}
          onChange={onChange}
        />
      </label>

      <span style={errorStyle}>{errorMsg}</span>
    </>
  );
}
