import React, { useState } from "react";
import FormField from "../../../../atom/formField";

export default function Info() {
  let [data, setData] = useState({
    name: "",
    phone: "",
    email: "",
    website: "",
    title: "",
    note: "",
  });

  let [error, setError] = useState({
    name: "",
    phone: "",
    email: "",
    website: "",
    title: "",
    note: "",
  });

  function getFormData(e) {
    //Onchange function
    data[e.target.name] = e.target.value;
    setData({ ...data });
  }

  function submit() {
    if (data.name === "") {
      error.name = "Họ và tên không được để trống";
      setError({ ...error });
    } else {
      error.name = "";
      setError({ ...error });
    }

    //phone
    if (data.phone === "") {
      error.phone = "Số điện thoại không được để trống";
      setError({ ...error });
    } else if (!/(0[3|5|7|8|9])+([0-9]{8})\b/.test(data.phone)) {
      error.phone = "Vui lòng điền đúng định dạng số điện thoại";
      setError({ ...error });
    } else {
      error.phone = "";
      setError({ ...error });
    }

    //email
    if (data.email === "") {
      error.email = "Email không được để trống";
      setError({ ...error });
    } else if (
      !/^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[gmail]+(?:\.[a-zA-Z0-9-]+)*$/.test(
        data.email
      )
    ) {
      error.email = "Vui lòng điền đúng định dạng gmail";
      setError({ ...error });
    } else {
      error.email = "";
      setError({ ...error });
    }

    //website
    if (data.website === "") {
      error.website = "Website không được để trống";
      setError({ ...error });
    } else if (
      !/^(http:\/\/www\.|https:\/\/www\.|http:\/\/|https:\/\/)?[a-z0-9]+([-.]{1}[a-z0-9]+)*\.[a-z]{2,5}(:[0-9]{1,5})?(\/.*)?$/.test(
        data.website
      )
    ) {
      error.website = "Vui lòng điền đúng định dạng link website";
      setError({ ...error });
    }

    //alert
  }

  return (
    <div className="tab1">
      <FormField
        label="Họ và Tên"
        type="text"
        name="name"
        placeholder="Nguyễn Văn A"
        required
        onChange={(e) => getFormData(e)}
        errorMsg={error.name}
      />
      <FormField
        label="Số điện thoại"
        type="text"
        name="phone"
        placeholder="0949******"
        required
        onChange={(e) => getFormData(e)}
        errorMsg={error.phone}
      />
      <FormField
        label="Email"
        type="text"
        name="email"
        placeholder="vuong.dang@dna.vn"
        required
        onChange={(e) => getFormData(e)}
        errorMsg={error.email}
      />
      <FormField
        label="Website"
        type="text"
        name="website"
        placeholder="Facebook url"
        required
        onChange={(e) => getFormData(e)}
        errorMsg={error.website}
      />
      <FormField
        label="Skype"
        type="text"
        name="website"
        placeholder="Skype url"
        required
        onChange={(e) => getFormData(e)}
        errorMsg={error.website}
      />

      <div className="btn main rect" onClick={() => submit()}>
        LƯU LẠI
      </div>
    </div>
  );
}
