import React, { useState } from "react";
import FormField from "../formField";

export default function Form() {
  let [data, setData] = useState({
    name: "",
    phone: "",
    email: "",
    website: "",
    title: "",
    note: "",
  });

  let [error, setError] = useState({
    name: "",
    phone: "",
    email: "",
    website: "",
    title: "",
    note: "",
  });

  function getFormData(e) {
    //Onchange function
    data[e.target.name] = e.target.value;
    setData({ ...data });
  }

  function submit() {
    if (data.name === "") {
      error.name = "Họ và tên không được để trống";
      setError({ ...error });
    } else {
      error.name = "";
      setError({ ...error });
    }

    //phone
    if (data.phone === "") {
      error.phone = "Số điện thoại không được để trống";
      setError({ ...error });
    } else if (!/(0[3|5|7|8|9])+([0-9]{8})\b/.test(data.phone)) {
      error.phone = "Vui lòng điền đúng định dạng số điện thoại";
      setError({ ...error });
    } else {
      error.phone = "";
      setError({ ...error });
    }

    //email
    if (data.email === "") {
      error.email = "Email không được để trống";
      setError({ ...error });
    } else if (
      !/^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[gmail]+(?:\.[a-zA-Z0-9-]+)*$/.test(
        data.email
      )
    ) {
      error.email = "Vui lòng điền đúng định dạng gmail";
      setError({ ...error });
    } else {
      error.email = "";
      setError({ ...error });
    }

    //website
    if (data.website === "") {
      error.website = "Website không được để trống";
      setError({ ...error });
    } else if (
      !/^(http:\/\/www\.|https:\/\/www\.|http:\/\/|https:\/\/)?[a-z0-9]+([-.]{1}[a-z0-9]+)*\.[a-z]{2,5}(:[0-9]{1,5})?(\/.*)?$/.test(
        data.website
      )
    ) {
      error.website = "Vui lòng điền đúng định dạng link website";
      setError({ ...error });
    }
  }

  return (
    <div className="form">
      <FormField
        label="Họ và Tên"
        type="text"
        name="name"
        placeholder="Họ và tên bạn"
        required
        onChange={(e) => getFormData(e)}
        errorMsg={error.name}
      />
      <FormField
        label="Số điện thoại"
        type="text"
        name="phone"
        placeholder="Số điện thoại"
        required
        onChange={(e) => getFormData(e)}
        errorMsg={error.phone}
      />
      <FormField
        label="Email"
        type="text"
        name="email"
        placeholder="Email của bạn"
        required
        onChange={(e) => getFormData(e)}
        errorMsg={error.email}
      />
      <FormField
        label="Website"
        type="text"
        name="website"
        placeholder="Đường dẫn website https://"
        required
        onChange={(e) => getFormData(e)}
        errorMsg={error.website}
      />
      <FormField
        label="Tiêu đề"
        type="text"
        name="title"
        placeholder="Tiêu đề liên hệ"
        onChange={(e) => getFormData(e)}
        errorMsg={error.title}
      />
      <FormField
        label="Nội dung"
        type="text"
        name="note"
        style={{ height: "150px" }}
        onChange={(e) => getFormData(e)}
      />

      <div className="btn main rect" onClick={() => submit()}>
        GỬI TIN
      </div>
    </div>
  );
}
