import React, { useState } from "react";
import FormField from "../../../../atom/formField";

export default function Info() {
  let [data, setData] = useState({
    name: "",
    phone: "",
    email: "",
    facebook: "",
    skype: "",
  });

  let [error, setError] = useState({
    name: "",
    phone: "",
    email: "",
    facebook: "",
    skype: "",
  });

  function getFormData(e) {
    //Onchange function
    data[e.target.name] = e.target.value;
    setData({ ...data });
  }

  function submit() {
    if (data.name === "") {
      error.name = "Họ và tên không được để trống";
      setError({ ...error });
    } else {
      error.name = "";
      setError({ ...error });
    }

    //phone
    if (data.phone === "") {
      error.phone = "Số điện thoại không được để trống";
      setError({ ...error });
    } else if (!/(0[3|5|7|8|9])+([0-9]{8})\b/.test(data.phone)) {
      error.phone = "Vui lòng điền đúng định dạng số điện thoại";
      setError({ ...error });
    } else {
      error.phone = "";
      setError({ ...error });
    }

    //email
    if (data.email === "") {
      error.email = "Email không được để trống";
      setError({ ...error });
    } else if (
      !/^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[gmail]+(?:\.[a-zA-Z0-9-]+)*$/.test(
        data.email
      )
    ) {
      error.email = "Vui lòng điền đúng định dạng gmail";
      setError({ ...error });
    } else {
      error.email = "";
      setError({ ...error });
    }

    //facebook
    if (data.facebook === "") {
      error.facebook = "Facebook không được để trống";
      setError({ ...error });
    } else if (
      !/^(http:\/\/www\.|https:\/\/www\.|http:\/\/|https:\/\/)?facebook\.[a-z]{2,5}(:[0-9]{1,5})?(\/.*)?$/.test(
        data.facebook
      )
    ) {
      error.facebook = "Vui lòng điền đúng định dạng link facebook";
      setError({ ...error });
    }
    // //skype
    // if (data.skype === "") {
    //   error.skype = "Skype không được để trống";
    //   setError({ ...error });
    // } else if (
    //   !/^(http:\/\/www\.|https:\/\/www\.|http:\/\/|https:\/\/)?skype\.[a-z]{2,5}(:[0-9]{1,5})?(\/.*)?$/.test(
    //     data.skype
    //   )
    // ) {
    //   error.skype = "Vui lòng điền đúng định dạng link facebook";
    //   setError({ ...error });
    // }

    //alert
  }

  return (
    <div className="tab1">
      <FormField
        label="Họ và Tên"
        type="text"
        name="name"
        placeholder="Nguyễn Văn A"
        required
        onChange={(e) => getFormData(e)}
        errorMsg={error.name}
      />
      <FormField
        label="Số điện thoại"
        type="text"
        name="phone"
        placeholder="0949******"
        required
        onChange={(e) => getFormData(e)}
        errorMsg={error.phone}
      />
      <FormField
        label="Email"
        type="text"
        name="email"
        placeholder="vuong.dang@dna.vn"
        required
        onChange={(e) => getFormData(e)}
        errorMsg={error.email}
      />
      <FormField
        label="Facebook"
        type="text"
        name="facebook"
        placeholder="Facebook url"
        required
        onChange={(e) => getFormData(e)}
        errorMsg={error.facebook}
      />
      <FormField
        label="Skype"
        type="text"
        name="skype"
        placeholder="Skype url"
        required
        onChange={(e) => getFormData(e)}
        errorMsg={error.website}
      />

      <div className="btn main rect" onClick={() => submit()}>
        LƯU LẠI
      </div>
    </div>
  );
}
