import React from "react";
import FormField from "../../../atom/formField";
import userApi from "../../../core/API/userApi";
import useFormValidate from "../../../hook/useFormValidate";

export default function Form() {
  let { form, inputChange, error, submit } = useFormValidate(
    {
      name: "",
      phone: "",
      email: "",
    },
    {
      rule: {
        name: {
          required: true,
        },
        phone: {
          required: true,
          pattern: "phone",
        },
        email: {
          required: true,
          pattern: "email",
        },
      },
      message: {
        name: {
          pattern: "Ho va ten không đúng định dạng",
        },
      },
    }
  );

  async function _btnClick(e) {
    e.preventDefault();
    console.log(form, "form");

    let error = submit();
    if (Object.keys(error).length === 0) {
      userApi.register(form, routerMatch.params.slug).then((res) => {
        console.log(res);
      });
    }

    setErrorBtnClick(error);
  }

  return (
    <div className="form">
      <FormField
        label="Họ và Tên"
        type="text"
        name="name"
        placeholder="Họ và tên bạn"
        required
        onChange={(e) => inputChange(e)}
        errorMsg={error.name}
      />
      <FormField
        label="Số điện thoại"
        type="text"
        name="phone"
        placeholder="Số điện thoại"
        required
        onChange={(e) => inputChange(e)}
        errorMsg={error.phone}
      />
      <FormField
        label="Email"
        type="text"
        name="email"
        placeholder="Email của bạn"
        required
        onChange={(e) => inputChange(e)}
        errorMsg={error.email}
      />
      <label>
        <p>Website</p>
        <input type="text" placeholder="Đường dẫn website http://" />
      </label>
      <label>
        <p>
          Tiêu đề<span>*</span>
        </p>
        <input type="text" placeholder="Tiêu đề liên hệ" />
      </label>
      <label>
        <p>
          Nội dung<span>*</span>
        </p>
        <textarea name id cols={30} rows={10} defaultValue={""} />
      </label>
      <div className="btn main rect" onClick={_btnClick(e)}>
        đăng ký
      </div>
    </div>
  );
}
